function insert_multiple_lines(array_lines, table_code, einvNumber){
    line_results = []
    println("Inserting Line")
    for ( i = 0; i < array_lines.length; i++){
        line_result = insert_line(array_lines[i], table_code, einvNumber)
        check_error = GetInsertErrorMessage(line_result, array_lines[i].comp, array_lines[i].tran, array_lines[i].idoc, array_lines[i].line)
        if (table_code == "einv_lines" && check_error.Status == true){
            println("Line Inserted Successfully")
            if (line_result.Message.cvat == "" || line_result.Message.cvat == undefined || line_result.Message.cvat == null){
                einvois.CreateValidation(einvNumber, array_lines[i].line, "10", "MAP", "Unmapped Tax Code", "InvoiceLine.Map", "cvat", array_lines[i].cvat)
            }
            if (line_result.Message.clas == "" || line_result.Message.clas == undefined || line_result.Message.clas == null){
                einvois.CreateValidation(einvNumber, array_lines[i].line, "10", "MAP", "Unmapped Classification Code", "InvoiceLine.Map", "clas", array_lines[i].clas)
            }
            println("After Map Validations")
        }
        line_results.push(check_error.Result)
    }
    return line_results
}

function GetInsertErrorMessage(result, comp, tran, idoc, line){
    if (typeof result == "string"){
        return {
            "Status": false,
            "Result": {
                "comp": comp,
                "tran": tran,
                "idoc": idoc,
                "line": line,
                "Message": result,
                "Success":false
            }
        }
    } else {
        if (result.Success == false){
            result.comp = comp
            result.tran = tran
            result.idoc = idoc
            result.line = line
            return {
                "Status": false,
                "Result": result
            }
        } else {
            return {
                "Status": true,
                "Result": result
            }
        }
    }
}

