function getOriginalDocumentReference(refDocNum){
    let invoice_number = ""
    let document_uuid = ""
    let refDocs = refDocNum.split(",")
    refDocs.forEach(data => {
        cancel_rec = CralDB.QueryByComp(`select e_inv_no from {{cancel_header}} where adcn='${data}'`, jscall.company)
        if (cancel_rec?.length){
            invoice_number = cancel_rec[0].e_inv_no
            org_document_uuid = CralDB.QueryByComp(`select doc_uuid from {{einv_header}} where e_inv_no='${cancel_rec[0].e_inv_no} and esta='30''`, jscall.company)
            if (org_document_uuid?.length){
                document_uuid = org_document_uuid[0].doc_uuid
            }
        }
    })
    return [invoice_number, document_uuid]
}
